import React, { useEffect, useState } from 'react';
import "./BookAppointment.css";
import { useParams } from 'react-router-dom';

export default function BookAppointment() {

  const { userId } = useParams();

  const [ doctors, setDoctors ] = useState([]);
  const [ selctedDoctor, setSelectedDocctor ] = useState();
  const [ doctorDetails, setDoctorDetails ] = useState([]);

  const [ year, setYear ] = useState('2024');
  const [ month, setMonth ] = useState('Jan');
  const [ day, setDay ] = useState('01');

  const years = [];
    for (let year = 2024; year <= 2024; year++) {
        years.push(year);
    }
    const months = ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun', 'Jul', 'Aug', 'Sep', 'Oct', 'Nov', 'Dec'];
    const days = [];
    for (let day = 1; day <=31; day++) {
        days.push(day);
    }

  const months_value = {
    'Jan' : "01",
    "Feb" : "02",
    "Mar" : "03",
    "Apr" : "04",
    "May" : "05",
    "Jun" : "06",
    "Jul" : "07",
    "Aug" : "08",
    "Sep" : "09",
    "Oct" : "10",
    "Nov" : "11",
    "Dec" : "12"
}


  useEffect(() => {
    const fetchAllDoctors = async () => {
      try {
        const responce = await fetch('http://localhost:5000/doctor/all', {
          method : "GET",
          headers: {
            'Content-Type': 'application/json'
          }
        });

        const responce_data = await responce.json();
        const x = [];
        x.push(responce_data.map((key) => {return key['name']}));
        setDoctors(x);
        setDoctorDetails(responce_data);
        setSelectedDocctor(x[0]);
      }
      catch (error) {
        console.log(error);
      }
    }

    fetchAllDoctors();
  }, [userId]);

  const changeDoctorname = (event) => {
      setSelectedDocctor(event.target.value);
  }

  const changeYear = (event) => {
    setYear(event.target.value);
  }
  const changeMonth = (event) => {
      setMonth(event.target.value);
  }
  const changeDay = (event) => {
    if (event.target.value.length === 1){
      setDay("0" + event.target.value);
    }
    else {

    }

  }

  const fetchSlots = async () => {
    let doc_id = 0;
    doctorDetails.map((key) => {
      if (key['name'] == selctedDoctor) {
        doc_id = key['id'];
      }
    })

    try {
      const responce = await fetch('http://localhost:5000/patient/view/availableslots', {
        method : "POST",
        headers: {
          'Content-Type': 'application/json'
        },
        body : JSON.stringify({
          "date" : year + months_value[month] + day,
          "doc_id" : doc_id
        })
      });

      const responce_data = await responce.json();
      console.log(responce_data['available_slots']);
    }
    catch (error) {
      console.log(error)
    }
  }

  return (
    <div id='bookappointment'>
        <label>Doctor : 
          <select onChange={changeDoctorname} value={selctedDoctor}>
            {doctors.map((doctor) => {return <option key={doctor} value={doctor}>{doctor}</option>})}
          </select>
        </label>
        <label>
          Date : 
          <div>
              <select value={year} onChange={changeYear}>
                  {years.map((key) => { return <option  value={key}>{key}</option> })}
              </select>
              <select value={month} onChange={changeMonth}>
                  {months.map((key) => { return <option value={key}>{key}</option> })}
              </select>
              <select value={day} onChange={changeDay}>
                  {days.map((key) => { return <option value={key}>{key}</option> })}
              </select>
          </div>
        </label>
        <button onClick={fetchSlots} >Fetch All Time Slots</button>
    </div>
  )
}
