import React, { useState } from 'react';
import "./SignupPage.css";

export default function SignupPage() {

    const [ phoneNo, setPhoneNo ] = useState();
    const [ otp, setOtp ] = useState();
    const [ username, setUsername ] = useState();
    const [ password, setPassword ] = useState(); 

    const handelChangePhoneNo = (event) => {
        setPhoneNo(event.target.value);
    }
    const handleChangeOtp = (event) => {
        setOtp(event.target.value);
    }
    const handleChangeUsername = (event) => {
        setUsername(event.target.value);
    }
    const handleChangePassword = (event) => {
        setPassword(event.target.value);
    }

    const handleVerify = async (e) => {
        e.preventDefault();
        document.getElementById('signupformotp').style.display = 'none';
        document.getElementById('signupformuser').style.display = 'flex';

        try {
            const responce = await fetch('http://localhost:5000/signup', {
                method : "POST",
                body : JSON.stringify({
                    phoneNo : phoneNo
                })
            })
        }
        catch (error) {
            console.log(error)
        }
    }

    const handleClick = (e) => {
        e.preventDefault();
        document.getElementById('signupformotp').style.display = 'flex';
        document.getElementById('signupformuser').style.display = 'none';
    }


  return (
    <section id='signpage'>
        <form id='signupformotp'>
            <input type='text' name='signpu_phoneno' placeholder='Phone Number' value={phoneNo} onChange={handelChangePhoneNo} />
            <input type='text' name='signup_otp' placeholder='OTP' value={otp} onChange={handleChangeOtp} />
            <button onClick={handleVerify}>Verify</button>
        </form>
        <form id="signupformuser">
            <input type='text' name='signup_username' placeholder='Username' value={username} onChange={handleChangeUsername}/>
            <input type='text' name='signup_password' placeholder='Password' value={password} onChange={handleChangePassword}/>
            <button onClick={handleClick}>Signup</button>
        </form>
    </section>
  )
}
