import React, { useState } from 'react';
import "./LoginPage.css";
import { useNavigate } from 'react-router-dom';

export default function LoginPage() {

  const navigate = useNavigate();

  const [ username, setUsername ] = useState('');
  const [ password, setPassword ] = useState('');

  const handleChangeUsername = (event) => {
    setUsername(event.target.value);
  }

  const handleChangePassword = (event) => {
    setPassword(event.target.value);
  }

  const handleClick = async (e) => {
    e.preventDefault();

    try {
      const responce = await fetch('http://localhost:5000/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          "role" : "patient",
          "username" : username,
          "password" : password
        })
      });

      const responce_data = await responce.json();

      console.log(responce_data['loginSuccess'])

      if  (responce_data['loginSuccess'] === 'True') {
        navigate('/home');
      }
    } 
    catch (e) {
      console.log(e)
    }
  }

  return (
    <section id='loginpage'>
      <form id="loginbox">
        <input type='text' name='login_username' placeholder='Username' value={username} onChange={handleChangeUsername}/>
        <input type='text' name='login_password' placeholder='Password' value={password} onChange={handleChangePassword}/>
        <button onClick={handleClick}>Login</button>
        <div id="errorbox">
          <p>Username or Password is wo</p>
        </div>
      </form>
    </section>
  )
}
