import React, { useState } from 'react';
import "./PersonalDetails.css";
import { useParams } from 'react-router-dom';

export default function PersonalDetails() {

    const { userId } = useParams();

    const [ name, setName ] = useState();
    const [ gender, setGender ] = useState();
    const [ dob, setDob ] = useState();
    const [ phoneNo, setPhoneNo ] = useState();
    const [ address, setAddress ] = useState();
    const [ email, setEmail ] = useState();

    const changeName = (event) => {
        setName(event.target.value);
    }

    const changeGender = (event) => {
        setGender(event.target.value);
    }

    const changeAddress = (event) => {
        setAddress(event.target.value);
    }

    const changeEmail = (event) => {
        setEmail(event.target.value);
        setGender('M');
    }

  return (
    <section id='personaldetails'>
        <form id='personaldetailsform' >
            <label>
                Name : <input type='text' value={name} name='name' onChange={changeName}/>
            </label>
            <label>
                Gender : <input type='radio' name='gender' value='M' onChange={changeGender} checked={gender === 'M'} /><label>Male</label>
                    <input type='radio' name='gender' value='F' onChange={changeGender} cha /><label>Felmale</label>
            </label>
            <label>
                Phone Number : <input type='number' name='phoneNo' min={1000000000} max={9999999999} value={phoneNo} />
            </label>
            <label>
                Address : <textarea type='text' name='address' value={address} onChange={changeAddress} />
            </label>
            <label>
                Email : <input type='email' name='email' value={email} />
            </label>
        </form>
    </section>
  )
}
