import React, { useState } from 'react';
import "./PersonalDetails.css";
import { useParams } from 'react-router-dom';

export default function PersonalDetails() {

    const { userId } = useParams();

    const [ name, setName ] = useState();
    const [ gender, setGender ] = useState();
    const [ dob, setDob ] = useState();
    const [ phoneNo, setPhoneNo ] = useState();
    const [ address, setAddress ] = useState();
    const [ email, setEmail ] = useState();

    const [ year, setYear ] = useState();
    const [ month, setMonth ] = useState()
    const [ day, setDay ] = useState();

    const years = [];
    for (let year = 1990; year <= 2024; year++) {
        years.push(year);
    }
    const months = [];
    for (let month = 1; month <= 12; month++) {
        months.push(month);
    }
    const days = [];
    for (let day = 1; day <=31; day++) {
        days.push(day);
    }

    const changeName = (event) => {
        setName(event.target.value);
    }

    const changeGender = (event) => {
        setGender(event.target.value);
    }

    const changeAddress = (event) => {
        setAddress(event.target.value);
    }

    const changeEmail = (event) => {
        setEmail(event.target.value);
    }

    const changeDob = (event) => {
        setDob(year + "-" + month + "-" + day)
    }

    const changeYear = (event) => {
        setYear(event.target.value);
    }
    const changeMonth = (event) => {
        setMonth(event.target.value);
    }
    const changeDay = (event) => {
        setDay(event.target.value);
    }

  return (
    <section id='personaldetails'>
        <form id='personaldetailsform' >
            <label>
                Name : <input type='text' value={name} name='name' onChange={changeName}/>
            </label>
            <label>
                Gender : <input type='radio' name='gender' value='M' onChange={changeGender} checked={gender === 'M'} /><label>Male</label>
                    <input type='radio' name='gender' value='F' onChange={changeGender} checked={gender === 'F'} /><label>Felmale</label>
            </label>
            <label>
                Date of Birth : 
                <select value={year} onChange={changeYear}>
                    {years.map((key) => { return <option value={key}>{key}</option> })}
                </select>
                <select value={month} onChange={changeMonth}>
                    {months.map((key) => { return <option value={key}>{key}</option> })}
                </select>
                <select value={day} onChange={changeDay}>
                    {days.map((key) => { return <option value={key}>{key}</option> })}
                </select>
            </label>
            <label>
                Phone Number : <input type='number' name='phoneNo' min={1000000000} max={9999999999} value={phoneNo} />
            </label>
            <label>
                Address : <textarea type='text' name='address' value={address} onChange={changeAddress} />
            </label>
            <label>
                Email : <input type='email' name='email' value={email} />
            </label>
        </form>
    </section>
  )
}
