import React, { useState } from 'react';
import "./LoginPage.css";

export default function LoginPage() {

  const [ username, setUsername ] = useState('');
  const [ password, setPassword ] = useState('');

  const handleChangeUsername = (event) => {
    setUsername(event.value);
  }

  const handleChangePassword = (event) => {
    setPassword(event.value);
  }

  const handleClick = async (e) => {
    const data = {
      "role" : "patient",
      "username" : username,
      "password" : password
    }
    e.preventDefault();

    try {
      const responce = await fetch('http://localhost:5000/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          "role" : "patient",
          "username" : username,
          "password" : password
        })
      });
      console.log(responce.json())
    } 
    catch (e) {
      console.log(e)
    }

  }

  return (
    <section id='loginpage'>
      <form id="loginbox">
        <input type='text' name='login_username' placeholder='Username' value={username} onChange={handleChangeUsername}/>
        <input type='text' name='login_password' placeholder='Password' value={password} onChange={handleChangePassword}/>
        <button onClick={handleClick}>Login</button>
      </form>
    </section>
  )
}
