import React, { useState } from 'react';
import "./LoginPage.css";
import { Await } from 'react-router-dom';

export default function LoginPage() {

  const [ username, setUsername ] = useState('');
  const [ password, setPassword ] = useState('');

  const handleChangeUsername = (event) => {
    setUsername(event.target.value);
  }

  const handleChangePassword = (event) => {
    setPassword(event.target.value);
  }

  const handleClick = async (e) => {
    e.preventDefault();

    try {
      const responce = await fetch(`http://localhost:5000/login?data=${({
        "role" : "patient",
        "username" : username,
        "password" : password
      })}`, {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json'
        }
      });

      const responce_data = await responce.json();

      console.log(responce_data['loginSuccess'])
    } 
    catch (e) {
      console.log(e)
    }

  }

  return (
    <section id='loginpage'>
      <form id="loginbox">
        <input type='text' name='login_username' placeholder='Username' value={username} onChange={handleChangeUsername}/>
        <input type='text' name='login_password' placeholder='Password' value={password} onChange={handleChangePassword}/>
        <button onClick={handleClick}>Login</button>
      </form>
    </section>
  )
}
